package com.example.demo;

import java.util.ArrayList;

import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.ResponseBody;


/*
 * Mappiamo la classe MyController con l'annotazione Controller
 */
@Controller
public class MyController {
	boolean started = false;
	// creiamo un arrayList di persona
	ArrayList <persona> lista = new ArrayList <>();
	ArrayList <personaStipendio> listaDip = new ArrayList <>();
	ArrayList <personaSelected> listaS = new ArrayList<>();
	ArrayList <prodottoImg> listaProdotti = new ArrayList <>();
	ArrayList <prodottoSelected> listaPS = new ArrayList<>();
	
	/*
	 * L'applicazione alla chiamata get verso localhost:8080
	 * risponderà con il file index
	 */
	@GetMapping("/")
	public String getIndex(Model m1) {
		if ( !started ) {
			// per semplicità aggiungo cose la prima volta che visito la home
			// persone predefinite
			listaDip.add(new personaStipendio("Gaia","Gialli", 1100,"https://cdn-icons-png.flaticon.com/512/5556/5556471.png"));
			listaDip.add(new personaStipendio("Marcello", "Indaco", 2050,"https://cdn-icons-png.flaticon.com/512/5556/5556475.png"));
			listaDip.add(new personaStipendio("Mariella","Porpora", 3000,"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQVVSJFEH6tLXAiddlbL2mJFReIM7DHIVo9wA&s"));
			listaDip.add(new personaStipendio("Giacomo", "Verdi", 800, "https://cdn-icons-png.flaticon.com/512/5556/5556512.png"));
			
			//prodotti predefiniti
			listaProdotti.add(new prodottoImg("Pane", "Grande Impero",2.87, "data:image/jpeg;base64,"));
			listaProdotti.add(new prodottoImg("Penne Rigate", "Barilla", 0.69,"data:image/jpeg;base64,"));
			listaProdotti.add(new prodottoImg("Lenticchie", "Colfiorito",2.99, "data:image/jpeg;base64,"));
			started = true;
		}
		
		// creiamo una stringa
		String nome = "Irene";
		
		/*
		 * La passiamo al model con la dicitura nome
		 * assegniamo alla variabile nome il suo valore
		 */
		
		m1.addAttribute("nome", nome);
		
		return "index";
	}
	/*
	 * L'applicazione alla chiamata get verso localhost:8080/contact
	 * risponderà con il file contact
	 */
	
	@GetMapping("/contact")
	public String getContact(Model m1) {
		
		String giorno = "Martedì";
		
		m1.addAttribute("giorno", giorno);
		return "contact";
	}
	/*
	 * L'applicazione alla chiamata get verso localhost:8080/blog
	 * risponderà con il file blog
	 */
	@GetMapping("/blog")
	public String getBlog(Model m1) {
		
		ArrayList <String> colori = new ArrayList <>();
		colori.add("Rosso");
		colori.add("Verde");
		colori.add("bianco");
		
		//passiamo la lista al file blog.html
		m1.addAttribute("lista", colori);
			
		return "blog";
	}
	
	/*
	 * Creimao una rotta che ritorna un form html
	 */
	@GetMapping("/form")
	public String getForm() {
		
		return("form");
	}
	/*
	 * andiamo a recuperare i dati dal form e con requestParam recuperiamo gli input di tipe text
	 * questo metodo va a recuperare i dati dal form con requestParam (in questo caso recuperiamo i dati dal name)
	 * Creimao una nuova persona
	 * la passiamo al model
	 */
	@PostMapping("/submit")
	public String getDati(@RequestParam("nome") String nome, 
			@RequestParam("cognome") String cognome, @RequestParam("stipendio") int stipendio,@RequestParam("url") String url, Model m1) {
		
		//System.out.println(nome);
		//System.out.println(cognome);
		
		personaStipendio p1 = new personaStipendio(nome, cognome, stipendio, url);
		listaDip.add(p1);
		
		m1.addAttribute("persona", p1);

		return("stampa");
	}
	
	@GetMapping("/lista")
	public String getLista (Model m1) {
		m1.addAttribute("lista",listaDip);
		// azzerriamo listaS
		listaS.clear();
		return "stampaL";
	}
	
	@GetMapping("/formProdotto")
	public String getFormProdotto() {
		
		return("formProdotto");
	}
	
	@PostMapping("/submitproduct")
	public String getDatiProdotto(@RequestParam("nome") String nome, 
			@RequestParam("marca") String marca, @RequestParam("prezzo") double prezzo, @RequestParam("url") String url, Model m1) {
				
		prodottoImg p1 = new prodottoImg(nome, marca, prezzo, url);
		listaProdotti.add(p1);
		
		m1.addAttribute("prodotto", p1);

		return("formProdotto");
	}
	
	@GetMapping("/listaProdotti")
	public String getListaProdotti (Model m1) {
		m1.addAttribute("lista",listaProdotti);
		listaPS.clear();
		return "stampaProdotti";
	}
		
	@PostMapping("/process")
	public String getDipendenti(@RequestParam("nome") ArrayList<String> nomi, 
			@RequestParam("num") ArrayList<Integer> numeri,
			//@RequestParam("url") String url,
			
			Model m1) {	
		/*
		 * Andiamo a iterare l'array nomi che ha lo stesso indice di numeri
		 * se il numero è diverso da zero (cioè se sono stati selezionati più dipendenti)
		 * Stampiamo il nome del dipedente selezionato
		 * il numero di dipedenti ad esso associato
		 */
		int somma = 0;
		for (int i = 0; i < nomi.size(); i++) {
			
			if (numeri.get(i) > 0) {
				
				System.out.println("Hai selezionato: " + nomi.get(i));
				System.out.println(numeri.get(i) + " volte");
				System.out.println("Il dipendente " + listaDip.get(i).nome
						+ " ha uno stipendio di "  + listaDip.get(i).stipendio);
				// la somma viene calcolata moltiplicando lo stipendio del dipendente selezionato con la quantità
				somma += numeri.get(i) * listaDip.get(i).stipendio;
				// creiamo una nuova personSelect con i dati e la aggiungiamo a listaS
				listaS.add(new personaSelected(listaDip.get(i).nome, listaDip.get(i).cognome, listaDip.get(i).stipendio, numeri.get(i),listaDip.get(i).url));
			}
		}
		
		// stampiamo la somma
		System.out.println("La somma degli stipendi è: " + somma + " euro");
				/*
				 * Nel model passiamo sia listaS che la somma
				 */
				m1.addAttribute("somma", somma);
				m1.addAttribute("lista", listaS);
				
				return("recap");		
	}
	
	@GetMapping("/getLista")
	@ResponseBody
	public ArrayList<personaStipendio> getLista(){
		return listaDip;
	}
	
	@PostMapping("/processProdotti")
	public String getProdotti(@RequestParam("nome") ArrayList<String> nomi, 
			@RequestParam("num") ArrayList<Integer> numeri,
			//@RequestParam("url") String url,
			
			Model m1) {	
		int somma = 0;
		for (int i = 0; i < nomi.size(); i++) {
			
			if (numeri.get(i) > 0) {
				
				System.out.println("Hai selezionato: " + nomi.get(i));
				System.out.println(numeri.get(i) + " volte");
				System.out.println("Il prodotto " + listaProdotti.get(i).nome
						+ " costa "  + listaProdotti.get(i).prezzo+" €");
				// la somma viene calcolata moltiplicando lo stipendio del dipendente selezionato con la quantità
				somma += numeri.get(i) * listaProdotti.get(i).prezzo;
				// creiamo una nuova personSelect con i dati e la aggiungiamo a listaS
				listaPS.add(new prodottoSelected(listaProdotti.get(i).nome, listaProdotti.get(i).marca, listaProdotti.get(i).prezzo, numeri.get(i),listaProdotti.get(i).url));
			}
		}
		
		// stampiamo la somma
		System.out.println("Prezzo totale: " + somma + " €");
				/*
				 * Nel model passiamo sia listaS che la somma
				 */
				m1.addAttribute("somma", somma);
				m1.addAttribute("lista", listaPS);
				
				return("recapP");		
	}
	
	@GetMapping("/getListaProdotti")
	@ResponseBody
	public ArrayList<prodottoImg> getListaProdotti(){
		return listaProdotti;
	}

}
